__NUXT_JSONP__("/blogNDI/post103", (function(a,b,c,d,e,f,g,h,i,j,k,l,m){return {data:[{article:{slug:"post103",tema:e,titolo:e,autore:"Gianluigi",parole:h,data:i,img:j,video:g,tags:g,toc:[],body:{type:k,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Andare verso una proibizione, venire verso una grandezza di egoismo e sopraffare il dolore."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost103",extension:m,createdAt:f,updatedAt:f},prev:{slug:"post101",tema:e,titolo:e,autore:"Alessia",parole:h,data:i,img:j,video:g,tags:g,toc:[],body:{type:k,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Vista con i miei occhi la follia umana è in ognuno di noi. Ognuno di noi che vedo, a volte, così diverso. È proprio questo gruppo di persone che mi dicono di andare più veloce ed io non li capisco. Vengono da me come se tutto fosse normale….Forse sono io la vera proibizione? Sono tutti diversi modi di essere, ma visto da me è uno sguardo unico che mi crea dolore dentro. No, non è egoismo, è soltanto voglia di non lasciarsi sopraffare da questa follia immensa. Mi sembra tutto così grande! E pensare che io sono così piccola, piccola."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost101",extension:m,createdAt:f,updatedAt:f},next:{slug:"post104",tema:e,titolo:e,autore:"Francesco P",parole:h,data:i,img:j,video:"https:\u002F\u002Fwww.youtube.com\u002Fembed\u002FVBRgvGuz23I",tags:g,toc:[],body:{type:k,children:[{type:b,tag:c,props:{},children:[{type:a,value:"ANDARE, VENIRE, andare, venire, andare, venire, ..."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Girotondo di momenti, attimi di luce, piccoli pensieri, grandi idee,\nricordi distorti, memoria dolorante, gambe stanche, ascelle sudate,\nun po' di deodorante."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Privazione o PROIBIZIONE del flusso incosciente ..."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Ricordi nel presente, attimi di stasi, occhi vivaci invisibili all'intorno,\nenergia proliferante dispersa nel buio."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"ESSERE, non essere, essere, non tessere, essere, non cessere, ..."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"il DOLORE alle tempie e' frutto del desiderio.\nDesiderio pensato incorniciato, fuori squadra a voler essere precisi.\nIl lungo silenzio dell'EGOISMO inonda le mie orecchie, dove un microscopico essere\nnaufraga, divenendo grido ossessivo e disturbante."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Mi faccio SOPPRAFFARE del sorriso ebete della memoria,\nguardo obliquamente la terra da cui nasco e rido bambinescamente."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Quale GRANDEZZA ho in me, quale meraviglia l'universo,\nquale immoto tempo risveglia il costante presente."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Ubicato nel profondo mezzo."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost104",extension:m,createdAt:f,updatedAt:f},show_parole:false}],fetch:{},mutations:void 0}}("text","element","p","\n","La follia umana","2021-04-03T08:03:34.903Z",null,"andare, venire, proibizione, essere, dolore, egoismo, sopraffare, grandezza","27 gennaio 2021","incertezza-1.jpg","root","\u002FarticlesNDI",".md")));