__NUXT_JSONP__("/blogNDI/post104", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n){return {data:[{article:{slug:"post104",tema:f,titolo:f,autore:"Francesco P",parole:k,data:l,img:m,video:"https:\u002F\u002Fwww.youtube.com\u002Fembed\u002FVBRgvGuz23I",tags:e,toc:[],body:{type:h,children:[{type:b,tag:c,props:{},children:[{type:a,value:"ANDARE, VENIRE, andare, venire, andare, venire, ..."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Girotondo di momenti, attimi di luce, piccoli pensieri, grandi idee,\nricordi distorti, memoria dolorante, gambe stanche, ascelle sudate,\nun po' di deodorante."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Privazione o PROIBIZIONE del flusso incosciente ..."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Ricordi nel presente, attimi di stasi, occhi vivaci invisibili all'intorno,\nenergia proliferante dispersa nel buio."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"ESSERE, non essere, essere, non tessere, essere, non cessere, ..."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"il DOLORE alle tempie e' frutto del desiderio.\nDesiderio pensato incorniciato, fuori squadra a voler essere precisi.\nIl lungo silenzio dell'EGOISMO inonda le mie orecchie, dove un microscopico essere\nnaufraga, divenendo grido ossessivo e disturbante."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Mi faccio SOPPRAFFARE del sorriso ebete della memoria,\nguardo obliquamente la terra da cui nasco e rido bambinescamente."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Quale GRANDEZZA ho in me, quale meraviglia l'universo,\nquale immoto tempo risveglia il costante presente."}]},{type:a,value:d},{type:b,tag:c,props:{},children:[{type:a,value:"Ubicato nel profondo mezzo."}]}]},dir:i,path:"\u002FarticlesNDI\u002Fpost104",extension:j,createdAt:g,updatedAt:g},prev:{slug:"post103",tema:f,titolo:f,autore:n,parole:k,data:l,img:m,video:e,tags:e,toc:[],body:{type:h,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Andare verso una proibizione, venire verso una grandezza di egoismo e sopraffare il dolore."}]}]},dir:i,path:"\u002FarticlesNDI\u002Fpost103",extension:j,createdAt:g,updatedAt:g},next:{slug:"post105",tema:e,titolo:"Qualcosa non quadra",autore:n,parole:"candeggina, luce, tenebre, sole, tappeto volante, posteggio, farfalla, imperfezione",data:"10 marzo 2021",img:"Incerto.jpg",video:e,tags:e,toc:[],body:{type:h,children:[{type:b,tag:c,props:{},children:[{type:a,value:"il sole splende verso la luce,\nverso un nero tenebre di colore\ntappeto volante: grandissimo\nposteggio di un volo di una\nfarfalla, ingiusta imperfezione\ndi colore di candeggina"}]}]},dir:i,path:"\u002FarticlesNDI\u002Fpost105",extension:j,createdAt:"2021-04-03T09:01:50.859Z",updatedAt:"2021-04-03T09:04:46.096Z"},show_parole:false}],fetch:{},mutations:void 0}}("text","element","p","\n",null,"La follia umana","2021-04-03T08:03:34.903Z","root","\u002FarticlesNDI",".md","andare, venire, proibizione, essere, dolore, egoismo, sopraffare, grandezza","27 gennaio 2021","incertezza-1.jpg","Gianluigi")));