__NUXT_JSONP__("/blogNDI/post108", (function(a,b,c,d,e,f,g,h,i,j,k,l){return {data:[{article:{slug:"post108",tema:d,titolo:j,autore:"Francesco P",parole:k,data:l,img:f,video:d,tags:d,toc:[],body:{type:g,children:[{type:b,tag:c,props:{},children:[{type:a,value:"E' mia ferma CONVINZIONE che un SOLDATO abbia una sua motivazione anche se egli direbbe\nessere dedizione, ma questa, in realta', e' solo una convenzione.\nCi puo' essere certo la sedizione data dallo stato di INADEGUATEZZA del soldato stesso,\nche percepisce una mutazione della motivazione seppure questa e' solo un mia deduzione."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Dopotutto non e' altro che un uomo IN DIVISA, e questa e' noto possiede il fascino della seduzione.\nE perche' mai non potrebbe il nostro uomo essere sedotto dal fascino corrotto di una setta NERA\na cui si e' ridotto per un alza bandiera?"}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Bandiera a mezz'asta, unitevi nel lutto, la patria e' sofferente, il popolo insorgente."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Alla mezzanotte intera, sotto una LUNA nera, ecco il popolo marciante ed esultante che\nsi muove rumoroso con certa convinzione. Ha la sua di motivazione. Sara' colpa del padrone!"}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"La folla compatta e indivisa marcia forte sul palazzo."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Sotto la luna le IMPRONTE di un soldato, scappato, isolato, poco motivato.\nForse lui non ha una convinzione e pure lui sta sotto un padrone."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Forse lui e' il principio della sedizione cui il popolo si e' unito con seria convinzione,\ne se stavolta non ci sara' un mutazione nella motivazione forse allora sara' vera\nrivoluzione."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"E cantando il popolo racconta che la parte nera e' ormai sconfitta, e col soldato marcia in fila dritta."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"... A conquistare la rossa primavera\nDove sorge il SOL(E) dell'avvenir ..."}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost108",extension:i,createdAt:"2021-04-03T09:10:55.053Z",updatedAt:"2021-04-03T09:11:22.359Z"},prev:{slug:"post107",tema:d,titolo:j,autore:"Luca",parole:k,data:l,img:f,video:d,tags:d,toc:[],body:{type:g,children:[{type:b,tag:c,props:{},children:[{type:a,value:"quando, con molta convinzione, il soldato\ndella legione straniera con molta\ninadeguatezza, in stato di ebrezza con una\nmeravigliosa divisa nera da capitano, con\nle stelline dorate che brillano con la\nluce della luna piena sulle impronte\ndelle scarpe che ha il sole"}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost107",extension:i,createdAt:"2021-04-03T09:09:38.688Z",updatedAt:"2021-04-03T09:10:47.520Z"},next:{slug:"post109",tema:d,titolo:"Qualcosa non quadra",autore:"Gianluigi",parole:"giocare, l'intellettualismo, calesse, cerchio, il cavallo nero, forte, straniamento, sinfonia",data:"3 marzo 2021",img:f,video:d,tags:d,toc:[],body:{type:g,children:[{type:b,tag:c,props:{},children:[{type:a,value:"giocare a carte  con l' intelletualismo molto\nsuperiore all' intelligenza. un calesse ï¿½ tirato dal\ncavallo nero e forte e in cerchio. un musicista, colto da un\nattimo di straniamento, compose una grande sinfonia in\nmusica"}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost109",extension:i,createdAt:"2021-04-03T09:11:33.579Z",updatedAt:"2021-04-03T09:13:26.576Z"},show_parole:false}],fetch:{},mutations:void 0}}("text","element","p",null,"\n","Incerto.jpg","root","\u002FarticlesNDI",".md","L'impossibile esistenza","convinzione, soldato, inadeguatezza, in divisa, nera, luna, impronte, sole","31 marzo 2021")));