__NUXT_JSONP__("/blogNDI/post109", (function(a,b,c,d,e,f,g,h,i,j,k,l,m){return {data:[{article:{slug:"post109",tema:f,titolo:"Qualcosa non quadra",autore:"Gianluigi",parole:"giocare, l'intellettualismo, calesse, cerchio, il cavallo nero, forte, straniamento, sinfonia",data:"3 marzo 2021",img:j,video:f,tags:f,toc:[],body:{type:g,children:[{type:b,tag:d,props:{},children:[{type:a,value:"giocare a carte  con l' intelletualismo molto\nsuperiore all' intelligenza. un calesse � tirato dal\ncavallo nero e forte e in cerchio. un musicista, colto da un\nattimo di straniamento, compose una grande sinfonia in\nmusica"}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost109",extension:i,createdAt:"2021-04-03T09:11:33.579Z",updatedAt:"2021-04-03T09:13:26.576Z"},prev:{slug:"post108",tema:f,titolo:"L'impossibile esistenza",autore:"Francesco P",parole:"convinzione, soldato, inadeguatezza, in divisa, nera, luna, impronte, sole",data:"31 marzo 2021",img:j,video:f,tags:f,toc:[],body:{type:g,children:[{type:b,tag:d,props:{},children:[{type:a,value:"E' mia ferma CONVINZIONE che un SOLDATO abbia una sua motivazione anche se egli direbbe\nessere dedizione, ma questa, in realta', e' solo una convenzione.\nCi puo' essere certo la sedizione data dallo stato di INADEGUATEZZA del soldato stesso,\nche percepisce una mutazione della motivazione seppure questa e' solo un mia deduzione."}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"Dopotutto non e' altro che un uomo IN DIVISA, e questa e' noto possiede il fascino della seduzione.\nE perche' mai non potrebbe il nostro uomo essere sedotto dal fascino corrotto di una setta NERA\na cui si e' ridotto per un alza bandiera?"}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"Bandiera a mezz'asta, unitevi nel lutto, la patria e' sofferente, il popolo insorgente."}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"Alla mezzanotte intera, sotto una LUNA nera, ecco il popolo marciante ed esultante che\nsi muove rumoroso con certa convinzione. Ha la sua di motivazione. Sara' colpa del padrone!"}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"La folla compatta e indivisa marcia forte sul palazzo."}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"Sotto la luna le IMPRONTE di un soldato, scappato, isolato, poco motivato.\nForse lui non ha una convinzione e pure lui sta sotto un padrone."}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"Forse lui e' il principio della sedizione cui il popolo si e' unito con seria convinzione,\ne se stavolta non ci sara' un mutazione nella motivazione forse allora sara' vera\nrivoluzione."}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"E cantando il popolo racconta che la parte nera e' ormai sconfitta, e col soldato marcia in fila dritta."}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"... A conquistare la rossa primavera\nDove sorge il SOL(E) dell'avvenir ..."}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost108",extension:i,createdAt:"2021-04-03T09:10:55.053Z",updatedAt:"2021-04-03T09:11:22.359Z"},next:{slug:"post11",tema:f,titolo:"Al mare",autore:"Rocchina",parole:"la solitudine, mi scaldo, surf, rilassatezza, sognare, il sole, trambusto, aquiloni",data:"11 novembre 2020",tags:f,img:"by-the-sea.jpg",toc:[{id:k,depth:2,text:l}],body:{type:g,children:[{type:b,tag:"h2",props:{id:k},children:[{type:b,tag:"a",props:{ariaHidden:"true",href:"#visioni-oniriche",tabIndex:-1},children:[{type:b,tag:"span",props:{className:["icon","icon-link"]},children:[]}]},{type:a,value:l}]},{type:a,value:e},{type:b,tag:d,props:{},children:[{type:a,value:"al mare la solitudine viene arrotolata"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nNell'onda e portata al largo."},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nCi si scalda  sul surf,"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nLa rilassatezza  non tarda ad arrivare;"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nSi comincia a sognare al sole,"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nFra il trambusto d'acquiloni."},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nUn bel respiro:ed ecco  l'energia"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nDella  nascita  per ricominciare."},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nQuanto è  profondo,l'apnea nello spazio onirico?"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nTrovo la magia:trasformo"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nLo \"svelo\" del gatto giocando"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nCon la sfera preziosa  dell' anello."},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nIl mio volo:nell' incoscienza"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nDalla terra in un vuoto viaggio, in una cascata"},{type:b,tag:c,props:{},children:[]},{type:a,value:"\nCon una pertica:sono in cielo!"}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost11",extension:i,createdAt:m,updatedAt:m},show_parole:false}],fetch:{},mutations:void 0}}("text","element","br","p","\n",null,"root","\u002FarticlesNDI",".md","Incerto.jpg","visioni-oniriche","VISIONI ONIRICHE","2021-04-03T08:03:34.903Z")));