__NUXT_JSONP__("/blogNDI/post13", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n){return {data:[{article:{slug:"post13",tema:c,titolo:k,autore:l,parole:m,data:f,tags:c,img:n,toc:[],body:{type:g,children:[{type:a,tag:h,props:{},children:[{type:b,value:"Datti una carica di energia per ricominciare!"},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nCon FATTIFORZA compresse rivestite potrai vivere una nuova nascita."},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nQuando ti senti in un profondo stato di spossatezza ... FATTIFORZA."},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nSe ti senti in apnea dalla vita ... FATTIFORZA"},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nAttenzione: leggere attentamente le avvertenze e le modalita' d'uso,"},{type:a,tag:e,props:{},children:[]},{type:b,value:"\npuo' indurre in uno spazio onirico definitivo."}]}]},dir:i,path:"\u002FarticlesNDI\u002Fpost13",extension:j,createdAt:d,updatedAt:d},prev:{slug:"post12",tema:c,titolo:"Al mare",autore:l,parole:"la solitudine, mi scaldo, surf, rilassatezza, sognare, il sole, trambusto, aquiloni",data:f,tags:c,img:"by-the-sea.jpg",toc:[],body:{type:g,children:[{type:a,tag:h,props:{},children:[{type:b,value:"Se vado al mare ci vado da solo. Nella solitudine mi scaldo guardando un uomo che prova\na fare surf su un mare appena mosso. Questa rilassatezza fa sognare, e non solo me.\nE' il sole che agisce. Almeno fino a quando il tentato surfista crea un'immenso trambusto ...\nHa preso il volo ed ora e' lassu', in cielo, in un mare di aquiloni."}]}]},dir:i,path:"\u002FarticlesNDI\u002Fpost12",extension:j,createdAt:d,updatedAt:d},next:{slug:"post14",tema:c,titolo:k,autore:"Satyamo",parole:m,data:f,tags:c,img:n,toc:[],body:{type:g,children:[{type:a,tag:h,props:{},children:[{type:b,value:"In un bel respiro l'energia cosmica irrompe in noi, portando le onde vitali, che ci permettono di ricominciare... nascita dopo nascita ad ogni respiro; morte dopo morte ad ogni esalazione, quando l'aria scende giù, giù, in profondità, fermandosi in'apnea per quella frazione di secondo...e tutto si ferma e lo spazio dentro di noi si unisce con l'universo che ci circonda ed il gioco onirico della vita diventa la danza della vita."}]}]},dir:i,path:"\u002FarticlesNDI\u002Fpost14",extension:j,createdAt:d,updatedAt:d},show_parole:false}],fetch:{},mutations:void 0}}("element","text",null,"2021-04-03T08:03:34.903Z","br","11 novembre 2020","root","p","\u002FarticlesNDI",".md","Un bel respiro","Francesco P","energia, ricominciare, nascita, quando, profondo, apnea, spazio, onirico","207655.jpg")));