__NUXT_JSONP__("/blogNDI/post14", (function(a,b,c,d,e,f,g,h,i,j,k,l,m){return {data:[{article:{slug:"post14",tema:c,titolo:f,autore:"Satyamo",parole:g,data:h,tags:c,img:i,toc:[],body:{type:j,children:[{type:a,tag:k,props:{},children:[{type:b,value:"In un bel respiro l'energia cosmica irrompe in noi, portando le onde vitali, che ci permettono di ricominciare... nascita dopo nascita ad ogni respiro; morte dopo morte ad ogni esalazione, quando l'aria scende giù, giù, in profondità, fermandosi in'apnea per quella frazione di secondo...e tutto si ferma e lo spazio dentro di noi si unisce con l'universo che ci circonda ed il gioco onirico della vita diventa la danza della vita."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost14",extension:m,createdAt:d,updatedAt:d},prev:{slug:"post13",tema:c,titolo:f,autore:"Francesco P",parole:g,data:h,tags:c,img:i,toc:[],body:{type:j,children:[{type:a,tag:k,props:{},children:[{type:b,value:"Datti una carica di energia per ricominciare!"},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nCon FATTIFORZA compresse rivestite potrai vivere una nuova nascita."},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nQuando ti senti in un profondo stato di spossatezza ... FATTIFORZA."},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nSe ti senti in apnea dalla vita ... FATTIFORZA"},{type:a,tag:e,props:{},children:[]},{type:b,value:"\nAttenzione: leggere attentamente le avvertenze e le modalita' d'uso,"},{type:a,tag:e,props:{},children:[]},{type:b,value:"\npuo' indurre in uno spazio onirico definitivo."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost13",extension:m,createdAt:d,updatedAt:d},next:{slug:"post15",tema:c,titolo:f,autore:"Abha",parole:g,data:h,tags:c,img:i,toc:[],body:{type:j,children:[{type:a,tag:k,props:{},children:[{type:b,value:"Un bel respiro e via! Altro giro altra corsa! Il miracolo della trasformazione, l’energia che si rinnova per ricominciare tutto daccapo, nascita dopo nascita! Chissà cosa succede quando riemergiamo dal profondo della nostra coscienza…è un po’ come riemergere dalle profondità del mare dopo una lunga apnea, tornare a risvegliarsi da uno spazio onirico in cui tutto è contemporaneamente presente, passato e futuro."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost15",extension:m,createdAt:d,updatedAt:d},show_parole:false}],fetch:{},mutations:void 0}}("element","text",null,"2021-04-03T08:03:34.903Z","br","Un bel respiro","energia, ricominciare, nascita, quando, profondo, apnea, spazio, onirico","11 novembre 2020","207655.jpg","root","p","\u002FarticlesNDI",".md")));