__NUXT_JSONP__("/blogNDI/post21", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{article:{slug:"post21",tema:f,titolo:l,autore:"Satyamo",parole:m,data:h,tags:f,img:n,toc:[],body:{type:i,children:[{type:b,tag:c,props:{},children:[{type:a,value:"\"Cerco un nome\", disse il vecchio barbone che perlustrava la spazzatura con scientifica meticolosità. \"Dicono che invece di cercare il fantastico là dove l'orizzonte raggiunge il cielo, bisogna penetrare il proprio angolo buio...cioè, connettersi con l'unico aggancio che noi poveri umani possiamo avere con lo spazio mu, dove la musica celestiale si nasconde...sì...fa la preziosa\".  \"Ma so che ci sono molto vicino e quindi pesco con tutto me stesso, per sovrastare il furore del vento che offusca la mia stessa esistenza, rendendomi, invece,  un assenza\"."}]}]},dir:j,path:"\u002FarticlesNDI\u002Fpost21",extension:k,createdAt:g,updatedAt:g},prev:{slug:"post20",tema:f,titolo:"Il mio volo",autore:"Francesco P",parole:"incoscienza, terra, cascata, sono, cielo, pertica, vuoto, viaggio",data:h,tags:f,img:"seagull-flying.jpg",toc:[],body:{type:i,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Fermo. Intento ad assorbire una nuvola. Guardo verso l'alto, ma non molto."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nSono in me. O forse sono nel tutto."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nINCOSCIENZA."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Appoggio i piedi sull'orizzonte."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nTERRA."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Mi scuoto. Sciolgo i capelli e con gesto della testa faccio fare loro un giro completo."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nCASCATA."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"IO SONO."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Dopo. Ritrovo il centro. Il centro incoscente della terra."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nCIELO."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"E sono di nuovo tutto. Non tra cielo e terra bensi' cielo e terra."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nPERTICA."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Sono universo."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nVUOTO."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"L'universo non si muove e se lo fa io non me ne accorgo. Perche' io sono universo."},{type:b,tag:d,props:{},children:[]},{type:a,value:"\nVIAGGIO."}]},{type:a,value:e},{type:b,tag:c,props:{},children:[{type:a,value:"Si un bel viaggio. Ho bisogno di fare un bel viaggio."}]}]},dir:j,path:"\u002FarticlesNDI\u002Fpost20",extension:k,createdAt:g,updatedAt:g},next:{slug:"post22",tema:f,titolo:l,autore:"Abha",parole:m,data:h,tags:f,img:n,toc:[],body:{type:i,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Cerco un nome per l’universo che ho creato, su un foglio bianco traccio i segni di un mondo fantastico dal cielo viola, ai confini un buio pieno di stelle, mi aggancio ad una di esse e volo via sulle ali di una musica meravigliosa, mentre petali di fiori di pesco vengono portati dal vento…"}]}]},dir:j,path:"\u002FarticlesNDI\u002Fpost22",extension:k,createdAt:o,updatedAt:o},show_parole:false}],fetch:{},mutations:void 0}}("text","element","p","br","\n",null,"2021-04-03T08:03:34.903Z","11 novembre 2020","root","\u002FarticlesNDI",".md","Cerco un nome","bianco, fantastico, cielo, buio, aggancio, musica, pesco, vento","Uomo-davanti-ad-un-punto-interrogativo.jpg","2021-04-03T08:03:34.907Z")));