__NUXT_JSONP__("/blogNDI/post22", (function(a,b,c,d,e,f,g,h,i,j,k,l,m){return {data:[{article:{slug:"post22",tema:a,titolo:c,autore:"Abha",parole:d,data:e,tags:a,img:f,toc:[],body:{type:g,children:[{type:h,tag:i,props:{},children:[{type:j,value:"Cerco un nome per l’universo che ho creato, su un foglio bianco traccio i segni di un mondo fantastico dal cielo viola, ai confini un buio pieno di stelle, mi aggancio ad una di esse e volo via sulle ali di una musica meravigliosa, mentre petali di fiori di pesco vengono portati dal vento…"}]}]},dir:k,path:"\u002FarticlesNDI\u002Fpost22",extension:l,createdAt:b,updatedAt:b},prev:{slug:"post21",tema:a,titolo:c,autore:"Satyamo",parole:d,data:e,tags:a,img:f,toc:[],body:{type:g,children:[{type:h,tag:i,props:{},children:[{type:j,value:"\"Cerco un nome\", disse il vecchio barbone che perlustrava la spazzatura con scientifica meticolosità. \"Dicono che invece di cercare il fantastico là dove l'orizzonte raggiunge il cielo, bisogna penetrare il proprio angolo buio...cioè, connettersi con l'unico aggancio che noi poveri umani possiamo avere con lo spazio mu, dove la musica celestiale si nasconde...sì...fa la preziosa\".  \"Ma so che ci sono molto vicino e quindi pesco con tutto me stesso, per sovrastare il furore del vento che offusca la mia stessa esistenza, rendendomi, invece,  un assenza\"."}]}]},dir:k,path:"\u002FarticlesNDI\u002Fpost21",extension:l,createdAt:m,updatedAt:m},next:{slug:"post23",tema:a,titolo:c,autore:"Luca",parole:d,data:e,tags:a,img:f,toc:[],body:{type:g,children:[{type:h,tag:i,props:{},children:[{type:j,value:"Cerco un nome che da nero trovi un aggancio per andare dal bianco dei fiori di pesco, fantastico che oscillando al vento fanno musica."}]}]},dir:k,path:"\u002FarticlesNDI\u002Fpost23",extension:l,createdAt:b,updatedAt:b},show_parole:false}],fetch:{},mutations:void 0}}(null,"2021-04-03T08:03:34.907Z","Cerco un nome","bianco, fantastico, cielo, buio, aggancio, musica, pesco, vento","11 novembre 2020","Uomo-davanti-ad-un-punto-interrogativo.jpg","root","element","p","text","\u002FarticlesNDI",".md","2021-04-03T08:03:34.903Z")));