__NUXT_JSONP__("/blogNDI/post28", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{article:{slug:"post28",tema:d,titolo:f,autore:"Luca",parole:g,data:h,tags:d,img:i,toc:[],body:{type:j,children:[{type:a,tag:k,props:{},children:[{type:b,value:"La gioia � una condizione che fa diventare il metallo oro tempestato di diamanti, escludere il rigugio, salire su un cavallo, essere in sella all'uragano al mattino."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost28",extension:m,createdAt:e,updatedAt:e},prev:{slug:"post27",tema:d,titolo:f,autore:"Abha",parole:g,data:h,tags:d,img:i,toc:[],body:{type:j,children:[{type:a,tag:k,props:{},children:[{type:b,value:"Credevo di poter trovare la gioia nel metallo tintinnante di un sacco di monete, o in quel diamante da 5 carati che mi aveva regalato…ma il disco d’oro del sole mi ha aperto gli occhi… quando mi son svegliata oggi, mentre bevevo il mio succo di frutta ho deciso di tornare al mio rifugio, lì dove da piccola imparai ad andare a cavallo in un mattino uggioso, quel cavallo fantastico che sembrava conoscermi da sempre e che quella volta mi portò in salvo sfuggendo all’uragano…."}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost27",extension:m,createdAt:e,updatedAt:e},next:{slug:"post29",tema:d,titolo:f,autore:"Rocchina",parole:g,data:h,tags:d,img:i,toc:[{id:n,depth:2,text:o}],body:{type:j,children:[{type:a,tag:"h2",props:{id:n},children:[{type:a,tag:"a",props:{ariaHidden:"true",href:"#il-cavallo",tabIndex:-1},children:[{type:a,tag:"span",props:{className:["icon","icon-link"]},children:[]}]},{type:b,value:o}]},{type:b,value:"\n"},{type:a,tag:k,props:{},children:[{type:b,value:"Cerco un  nome, fantastico nel cielo buio c è un aggancio di musica  bianca; pesco vento..."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nDov è  la gioia?"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nDal cavallo si chiamerà uragano,"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nVado al rifugio, sorseggio il succo di  frutta; prendo il bastone di metallo e oro con diamante."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nCosi Comincia la  gioia!"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nNasconde l 'amore nei  rovi, uragano è sangue libero passione, vuole giocare mangiando le more tra le   spine- che amore-."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nQuesta settimana  lo devo plasmare."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nMarcano il tempo le zampette di uragano, insieme ai secondi deve seguire la bussola; devono diventare  pistone, con il fluire della stagione."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nCon l 'esperienza saranno eliche!"}]}]},dir:l,path:"\u002FarticlesNDI\u002Fpost29",extension:m,createdAt:e,updatedAt:e},show_parole:false}],fetch:{},mutations:void 0}}("element","text","br",null,"2021-04-03T08:03:34.907Z","Dov'e' la gioia","metallo, diamante, oro, succo di frutta, rifugio, cavallo, mattino, uragano","11 novembre 2020","207647.jpg","root","p","\u002FarticlesNDI",".md","il-cavallo","Il cavallo")));