__NUXT_JSONP__("/blogNDI/post4", (function(a,b,c,d,e,f,g,h,i,j,k,l,m){return {data:[{article:{slug:"post4",tema:d,titolo:"Follia",autore:"Rocchina",parole:"Pazzia, incattivisce, verde, pericolo, divertimento, società, amore, buio",data:"11 novembre 2020",img:"pexels-elina-krima.jpg",tags:d,toc:[],body:{type:f,children:[{type:a,tag:g,props:{},children:[{type:b,value:"C'è la  follia  del divertimento"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nnella società dell'amore al buio."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nChe genera pazzia, incattivisce"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nSvanisce il verde rimane il pericolo."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\ndi una società alterata,"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\ngenerazuone vacua,"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nuna gabbia di fallimento e ansietà;"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nsenza campo d'aiuto."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nLa fantasia dell'amore è il bene,"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nrumore di libertà, risate,"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nbellezza della vita; di cieli azzurri."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nFino alla  pandemia: purtroppo"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nCovid 19 e tutto è in pericolo!"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nGuerra per scombinare  l'unione"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\ndella colla del nostro esistere."},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nCoprifuoco!"},{type:a,tag:c,props:{},children:[]},{type:b,value:"\nSfida alla  realtà!"}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost4",extension:i,createdAt:e,updatedAt:e},prev:{slug:"post39",tema:d,titolo:j,autore:"Abha",parole:k,data:l,img:m,tags:d,toc:[],body:{type:f,children:[{type:a,tag:g,props:{},children:[{type:b,value:"Ero eccitatissima per la partenza, avevo dormito pochissimo! Era trascorso appena un mese da quando ci eravamo visti l’ultima volta, ma a me pareva un tempo infinito! Guardavo le nuvole nere dalla finestra, sperando che non si mettesse a piovere proprio quando sarei uscita di casa… Purtroppo la legge di Murphy non si smentì e ancora una volta confermò il suo nome: non appena uscii di casa un grosso tuono fece scattare gli allarmi delle auto della mia strada, e pochi secondi dopo iniziò a scendere una pioggia scrosciante. Con la borsa a tracolla, lo zaino in spalla e il trolley che saltava sul marciapiede rotto cominciai a correre verso la stazione; inutile tentare di prendere un taxi, alle prime gocce di pioggia diventava impossibile trovarne uno libero, avrei fatto prima a piedi… Il treno doveva partire alle 9:08, ma il mio dramma era già cominciato…"}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost39",extension:i,createdAt:e,updatedAt:e},next:{slug:"post40",tema:d,titolo:j,autore:"Luca",parole:k,data:l,img:m,tags:d,toc:[],body:{type:f,children:[{type:a,tag:g,props:{},children:[{type:b,value:"Le nuvole vanno all'infinito nel mio cervello, ma su un treno ho trovato una borsa che contiene la mia felicità però scendendo il gradino della carozza, l'ho persa.  E' stato un dramma."}]}]},dir:h,path:"\u002FarticlesNDI\u002Fpost40",extension:i,createdAt:e,updatedAt:e},show_parole:false}],fetch:{},mutations:void 0}}("element","text","br",null,"2021-04-03T08:03:34.907Z","root","p","\u002FarticlesNDI",".md","Partenza","infinito, viaggio, le nuvole, gradino, borsa, treno, dramma","6 gennaio 2021","pexels-miguel-á-padriñán.jpg")));